$(document).ready(function(){$(".footnoted").each(function(e){var t=e+1;$(this).html("<a>"+t+"</a>")});$(".footnoted a").each(function(e){this.href="#footnote-"+(e+1)});$("#footnotes li").each(function(e){this.id="footnote-"+(e+1)});$("#footnotes li").last().after('<li id="back-to-top"><a>&uarr;&nbsp;&nbsp;Back to top</a></li>');$("#back-to-top").click(function(){$("html, body").animate({scrollTop:0},"fast")});$('.footnoted a[href^="#"]').on("click",function(e){e.preventDefault();var t=this.hash,n=$(t);$("html, body").stop().animate({scrollTop:n.offset().top},900,"swing",function(){window.location.hash=t})})});